<?php     require_once(__DIR__ . "/model/RecipeBrowseView.php");        if( !isset($_GET['page']) || !isset($_GET['type']) )    {        header("Location: http://{$_SERVER['HTTP_HOST']}/Recipe/404/");    }    if( empty($_GET['page']) )    {        $_GET['page'] = 1;    }        if( isset($_GET["category"]) && isset($_GET["order"]) && $_GET['page'] > 0 )    {        $view = new RecipeBrowseView( $_GET['page'], $_GET["order"], $_GET["category"]);    }    else if( isset($_GET["order"]) && $_GET['page'] > 0 )    {           $view = new RecipeBrowseView($_GET['page'], $_GET["order"]);    }    else if( $_GET['page'] > 0 )    {           $view = new RecipeBrowseView($_GET['page']);    }    else    {           $view = new RecipeBrowseView();     }        if( $_GET['type'] == "All" )    {        $view->populateBrowseRecipe();     }    else if( $_GET['type'] == "My" )     {           $view->populateMyRecipe();     }?>